import {useEffect,useState,useRef} from "react";
import axios from "axios";
import Image from 'react-bootstrap/Image'
import {Row,Container,Col,Placeholder,Card,Button, FormSelect,Form} from 'react-bootstrap'

import 'bootstrap/dist/css/bootstrap.min.css';

const App: React.FC = () => {

    const [book,setBook] = useState("");
    const [result, setResult] = useState([])
    const [apiKey, setApiKey] = useState("AIzaSyBoEqIGK_Gc7hMaEirCfDziPxMqHBpNhN4")
    const [totalItems,setTotalItems]=useState(0);
    const [filter,setFilter] = useState("all")

    const handleChange = (event:any)=>{
        const book = event.target.value;

        setBook(book);
    }
    const handleSubmit = (event:any)=>{
        event.preventDefault();
        axios.get("https://www.googleapis.com/books/v1/volumes?q="+book+"&key="+apiKey+"&maxResults=40")
            .then((data:any) => {
                console.log(data.data.items);
                setResult(data.data.items);
                setTotalItems(data.data.totalItems);
            })
    }

    return (
        <div className="container">
            <h1>Поиск книг</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input
                        type={"text"}
                        onChange={handleChange}
                        className="form-control mt-10"
                        placeholder={"Введите названиие книги"}
                        autoComplete={"off"}/>
                </div>
                <div >

                    <Form.Select
                        aria-label="Default select example"
                    onChange={(event:any)=>{
                        setFilter(event.target.value)
                        console.log(filter)
                    }}>
                        <option>all</option>
                        <option value="art">art</option>
                        <option value="biography">biography</option>
                        <option value="computers">computers</option>
                        <option value="history">history</option>
                        <option value="medical">medical</option>
                        <option value="poetry">poetry</option>
                    </Form.Select> 
                    <Form.Select aria-label="Default select example">
                        <option>relevance </option>
                        <option value="newest">newest</option>

                    </Form.Select>
                </div>
                <br/>
                <button
                    type={"submit"}
                    className="btn btn-danger">
                    Поиск
                </button>
            </form>
            { totalItems>0 && <h5 style={{textAlign:"center"}} > Общее количество {totalItems}</h5>}
            <Container fluid >
                <Row className="justify-content-md-center g-3">
                {result.map((book:any)=>(
                    <Card style={{ width: '15rem' }}>
                        <Card.Img variant="top" src={book.volumeInfo?.imageLinks?.thumbnail} />
                        <Card.Body>
                            <Card.Text>

                                {book?.volumeInfo?.categories!=undefined && book?.volumeInfo?.categories[0]}
                            </Card.Text>
                        <Card.Title>{book.volumeInfo.title}</Card.Title>
                            <Card.Text>
                            {book.volumeInfo?.authors?.map((author:any)=>(
                                author
                            ))}
                            <br/>
                            </Card.Text>
                         <Button variant="primary">Go somewhere</Button>
                        </Card.Body>
                    </Card>
                ))}
                </Row>
            </Container>
        </div>
    );
}

export {App}
