import {useEffect,useState,useRef} from "react";
import axios from "axios";
import {Row,Container,Col,Card,Button, InputGroup,FormControl,Form,FormText} from 'react-bootstrap'
import store from "./store/Store"

import 'bootstrap/dist/css/bootstrap.min.css';
import Footer from "./components/Footer";
import Header from "./components/Header";

const App: React.FC = () => {





    const handleChange = (event:any)=>{
        const book = event.target.value;
        setBook(book);
    }

    const handleSubmit = (event:any)=>{
        event.preventDefault();
        setStartIndex(0);
        setTotalItems(0);
        setIsFetching(true);

    }
    useEffect(()=>{
        if (isFetching) {
            getBooks().then((data: any) => {
                setResult(data.data.items);
                setTotalItems(data.data.totalItems);
            })
            setStartIndex(startIndex+maxResults);
            setIsFetching(false);
        }
    })

    return (
        <div className="container">
            <Header/>
            <h1 >Поиск книг</h1>

            <form onSubmit={handleSubmit}>
                <InputGroup className="mb-3">
                    <FormControl
                        type={"text"}
                        onChange={handleChange}
                        placeholder="Введите название книги"
                    />
                    <Button
                        type={"submit"}
                        variant="light"
                        id="button-addon2">
                        Поиск
                    </Button>
                </InputGroup>

                <Container fluid >
                    <Row>
                        <Col style={{
                            textAlign: 'right',
                        }}>
                            Категория
                        </Col>
                        <Col >
                            <Form.Select
                                aria-label="Default select example"
                                onChange={(event:any)=>{
                                    setFilter(event.target.value)

                                }}>
                                <option value="all">Все</option>
                                <option value="art">Искусство</option>
                                <option value="biography">Биография</option>
                                <option value="computers">Компьютеры</option>
                                <option value="history">История</option>
                                <option value="medical">Медицина</option>
                                <option value="poetry">Поэзия</option>
                            </Form.Select>
                        </Col>
                        <Col style={{
                            textAlign: 'right',
                        }}>
                            Сортировать по
                        </Col>
                        <Col>
                            <Form.Select
                                aria-label="Default select example"
                                onChange={(event:any)=>{
                                    setSort(event.target.value)

                                }}>
                                <option value="relevance">Актуальный</option>
                                <option value="newest">Новейший</option>
                            </Form.Select>
                        </Col>
                    </Row>
                </Container>
            </form>
            <p/>

            { totalItems>0 && <h5 style={{textAlign:"center"}} > Общее количество {totalItems}</h5>}
            <p/>
            <Container fluid >
                <Row className="justify-content-md-center g-3">
                {result.map((book:any)=>(
                    <Col>
                        <Card style={{ width: '300px', height: "100%"}} bg={'Light'} border="dark">
                            <Card.Img height={'230px'} variant="top" src={book.volumeInfo?.imageLinks?.thumbnail} />
                            <Card.Body>
                                <Card.Text>
                                    <u>{book?.volumeInfo?.categories!=undefined && book?.volumeInfo?.categories[0]}</u>
                                </Card.Text>
                                <Card.Title ><p className={'fs-6'}>{book.volumeInfo.title}</p></Card.Title>
                                <Card.Text>
                                {book.volumeInfo?.authors?.map((author:any)=>(
                                    <FormText className={'text-sm-center'}>{author}</FormText>
                                ))}
                                <br/>
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
                </Row>
            </Container>
            <br/>
            <Row>
                {startIndex>0 && startIndex<totalItems &&<Button
                    variant="warning"
                    size="lg"
                    onClick={(event)=>{
                        let copyResult = Object.assign([], result);
                        let copyStartIndex = startIndex+maxResults;
                        setStartIndex(copyStartIndex);
                        getBooks().then((data:any) => {
                            setTotalItems(data.data.totalItems);
                            copyResult=copyResult.concat(data.data.items);
                            setResult(copyResult);
                        })
                    }}
                >
                    Загрузить еще
                </Button>}
            </Row>
            <Footer/>
        </div>
    );
}

export {App}
