import {useEffect,useState,useRef} from "react";
import axios from "axios";
import Image from 'react-bootstrap/Image'
import {Row,Container,Col,Placeholder,Card,Button} from 'react-bootstrap'

import 'bootstrap/dist/css/bootstrap.min.css';

const App: React.FC = () => {

    const [book,setBook] = useState("");
    const [result, setResult] = useState([])
    const [apiKey, setApiKey] = useState("AIzaSyBoEqIGK_Gc7hMaEirCfDziPxMqHBpNhN4")
    const [totalItems,setTotalItems]=useState(0);

    const handleChange = (event:any)=>{
        const book = event.target.value;

        setBook(book);
    }
    const handleSubmit = (event:any)=>{
        event.preventDefault();
        axios.get("https://www.googleapis.com/books/v1/volumes?q="+book+"&key="+apiKey+"&maxResults=40")
            .then((data:any) => {
                console.log(data.data.items);
                setResult(data.data.items);
                setTotalItems(data.data.totalItems);
            })
    }

    return (
        <div className="container">
            <h1>Поиск книг</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input
                        type={"text"}
                        onChange={handleChange}
                        className="form-control mt-10"
                        placeholder={"Введите названиие книги"}
                        autoComplete={"off"}/>
                </div>
                <button
                    type={"submit"}
                    className="btn btn-danger">
                    Поиск
                </button>
            </form>

            { totalItems>0 && <h2 > Общее количество {totalItems}</h2>}
            {/*<Container>*/}
            {/*    <Row>*/}
            {/*        {result.map((book:any)=>(*/}
            {/*            <Col >*/}
            {/*                <Image src={book.volumeInfo?.imageLinks?.thumbnail} rounded  />*/}
            {/*                <h2>{book.volumeInfo.title}</h2>*/}
            {/*                {console.log(book.volumeInfo?.authors)}*/}
            {/*                {book.volumeInfo?.authors?.map((author:any)=>(*/}
            {/*                    <h2>{author}</h2>*/}
            {/*                ))}*/}
            {/*                {book?.volumeInfo?.categories!=undefined && <h3>{book?.volumeInfo?.categories[0]}</h3>}*/}
            {/*            </Col>*/}


            {/*        ))}*/}

            {/*    </Row>*/}
            {/*</Container>*/}
            {/*<div className="d-flex justify-content-around">*/}
                {result.map((book:any)=>(
                    <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={book.volumeInfo?.imageLinks?.thumbnail} />
                        <Card.Body>
                        <Card.Title>{book.volumeInfo.title}</Card.Title>
                        <Card.Text>
                            {book.volumeInfo?.authors?.map((author:any)=>(
                                author
                                ))}
                            {book?.volumeInfo?.categories!=undefined && <h3>{book?.volumeInfo?.categories[0]}</h3>}
                        </Card.Text>
                         <Button variant="primary">Go somewhere</Button>
                        </Card.Body>
                    </Card>
                ))}
            {/*</div>*/}
        </div>
    );
}

export {App}
