import React, {useEffect, useState} from "react";
import {observer} from "mobx-react-lite";
import store from "../store/Store"
import {Col, Form, Image} from "react-bootstrap";



const BookItem: React.FC = observer((props) => {
    const [book,setBook]=useState(store.books[0]);
    useEffect(()=>  {
        let pathName=window.location.pathname
        let indexBook:number= parseInt(pathName.substr (-(pathName.length-pathName.indexOf('=')-1)));
        console.log(store.books[indexBook])
        setBook(store.books[indexBook]);
    })
    return (
        <div className="container">
            Hello world{book?.volumeInfo?.title}
            <p/>
            <Image
                style={{width:"50%"}}
                src={book.volumeInfo?.imageLinks?.thumbnail}
                className="img-fluid rounded-start fa-layers-bottom-left" fluid />
            <Col>
                <Form.Select
                    onChange={(event:any)=>{
                        store.setSort(event.target.value)

                    }}>
                    <option value="relevance">Актуальный</option>
                    <option value="newest">Новейший</option>
                </Form.Select>
            </Col>
        </div>
    );
})

export default BookItem;
