import {useEffect,useState,useRef} from "react";
import axios from "axios";
import Image from 'react-bootstrap/Image'
import {Row,Container,Col} from 'react-bootstrap'

import 'bootstrap/dist/css/bootstrap.min.css';

const App: React.FC = () => {

    const [book,setBook] = useState("");
    const [result, setResult] = useState([])
    const [apiKey, setApiKey] = useState("AIzaSyBoEqIGK_Gc7hMaEirCfDziPxMqHBpNhN4")
    const [totalItems,setTotalItems]=useState(0);

    const handleChange = (event:any)=>{
        const book = event.target.value;

        setBook(book);
    }
    const handleSubmit = (event:any)=>{
        event.preventDefault();
        axios.get("https://www.googleapis.com/books/v1/volumes?q="+book+"&key="+apiKey+"&maxResults=40")
            .then((data:any) => {
                console.log(data.data.items);
                setResult(data.data.items);
                setTotalItems(data.data.totalItems);
            })
        console.log(book);
    }

    return (
        <div className="container">
            <h1>Поиск книг</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input
                        type={"text"}
                        onChange={handleChange}
                        className="form-control mt-10"
                        placeholder={"Введите названиие книги"}
                        autoComplete={"off"}/>
                </div>
                <button
                    type={"submit"}
                    className="btn btn-danger">
                    Поиск
                </button>
            </form>

            <h2 className={'align-content-center'}> Общее количество {totalItems}</h2>
            <Container>
                <Row>
                    {result.map((book:any)=>(
                        <Col >
                            <Image src={book.volumeInfo?.imageLinks?.thumbnail} rounded  />
                            <h2>{book.volumeInfo.title}</h2>
                            {console.log(book.volumeInfo?.authors)}
                            {book.volumeInfo?.authors?.map((author:any)=>(
                                <h2>{author}</h2>
                            ))}
                            <h2>{book.volumeInfo?.categories[0]}</h2>
                        </Col>

                    ))}

                </Row>
            </Container>
        </div>
    );
}

export {App}
